#!/bin/bash

echo '========================================================'
echo '   Updating Gain12'
echo '========================================================'

cd /vagrant
echo '-> Updating composer'
sudo composer self-update
sudo composer install --dev

echo '--------------------------------------------------------'
echo '-> Updating database'
php gain12/database/update.php

echo '--------------------------------------------------------'
echo '-> Building CSS/JS'
grunt

